import React, { useState, useEffect } from 'react';
import { MdMic, MdPause, MdStop } from 'react-icons/md'; // Import microphone and other icons from react-icons library

const SpeechToText = () => {
  const [transcript, setTranscript] = useState('');
  const [isListening, setIsListening] = useState(false);

  useEffect(() => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onresult = event => {
      const transcript = Array.from(event.results)
        .map(result => result[0].transcript)
        .join('');
      setTranscript(transcript);
    };

    if (isListening) {
      recognition.start();
    } else {
      recognition.stop();
    }

    return () => {
      recognition.stop();
    };
  }, [isListening]);

  const toggleListening = () => {
    setIsListening(prevState => !prevState);
  };

  const clearTranscript = () => {
    setTranscript('');
  };

  return (
    <div>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <button onClick={toggleListening} style={{ backgroundColor: isListening ? 'red' : 'blue', border: 'none', marginRight: '5px' }}>
          {isListening ? <MdPause size={24} /> : <MdMic size={24} />} {/* Render microphone or pause icon based on listening state */}
        </button>
        <button onClick={clearTranscript} style={{ backgroundColor: 'gray', border: 'none', marginRight: '5px' }}>
          <MdStop size={24} /> {/* Render stop icon */}
        </button>
        <label>{transcript}</label> {/* Display the recognized speech transcript as label text */}
      </div>
    </div>
  );
};

export default SpeechToText;
