import React, { useState, useEffect } from 'react';
import { MdMic, MdPause, MdStop } from 'react-icons/md';

const SpeechToText = () => {
    const [transcripts, setTranscripts] = useState(Array.from({ length: 5 }, () => ''));
    const [isListening, setIsListening] = useState(Array.from({ length: 5 }, () => false));
    const [recognitions, setRecognitions] = useState([]);

    useEffect(() => {
        const newRecognitions = Array.from({ length: 5 }, () => new window.webkitSpeechRecognition());
        newRecognitions.forEach((recognition, index) => {
            recognition.continuous = true;
            recognition.interimResults = true;
            recognition.lang = 'en-US';

            recognition.onstart = () => {
                console.log(`Recognition ${index} started.`);
                setIsListening(prevState => prevState.map((state, i) => (i === index ? true : state)));
            };

            recognition.onend = () => {
                console.log(`Recognition ${index} ended.`);
                setIsListening(prevState => prevState.map((state, i) => (i === index ? false : state)));
            };

            recognition.onresult = event => {
                const newTranscript = Array.from(event.results)
                    .map(result => result[0].transcript)
                    .join('');
                console.log(`Transcript for Recognition ${index}:`, newTranscript);
                setTranscripts(prevTranscripts => {
                    const updatedTranscripts = [...prevTranscripts];
                    updatedTranscripts[index] = newTranscript;
                    return updatedTranscripts;
                });
            };
        });

        setRecognitions(newRecognitions);

        // return () => {
        //     newRecognitions.forEach(recognition => recognition.stop());
        // };
    }, []);

    console.log(transcripts);

    const toggleListening = index => {
        if (!isListening[index]) {
            recognitions[index].start();
        } else {
            recognitions[index].stop();
        }
    };

    const clearTranscript = index => {
        setTranscripts(prevTranscripts => {
            const updatedTranscripts = [...prevTranscripts];
            updatedTranscripts[index] = '';
            return updatedTranscripts;
        });
    };

    return (
        <div>
            {[...Array(5)].map((_, index) => (
                <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                    <button onClick={() => toggleListening(index)} style={{ backgroundColor: isListening[index] ? 'red' : 'blue', border: 'none', marginRight: '5px' }}>
                        {isListening[index] ? <MdPause size={24} /> : <MdMic size={24} />}
                    </button>
                    <button onClick={() => clearTranscript(index)} style={{ backgroundColor: 'gray', border: 'none', marginRight: '5px' }}>
                        <MdStop size={24} />
                    </button>
                    <label>{transcripts[index]}</label>
                </div>
            ))}
        </div>
    );
};

export default SpeechToText;
