import React, { useState, useEffect } from 'react';
import { MdMic, MdPause, MdStop } from 'react-icons/md';

const SpeechToText = () => {
  const [transcripts, setTranscripts] = useState(Array.from({ length: 5 }, () => ''));
  const [isListening, setIsListening] = useState(false);
  const [isPaused, setIsPaused] = useState(false);

  useEffect(() => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onresult = event => {
      const newTranscripts = Array.from({ length: event.results.length }, (_, index) => {
        return Array.from(event.results[index]).map(result => result.transcript).join('');
      });

      setTranscripts(prevTranscripts => {
        const updatedTranscripts = [...prevTranscripts];
        newTranscripts.forEach((transcript, index) => {
          updatedTranscripts[index] = transcript;
        });
        return updatedTranscripts;
      });
    };

    if (isListening && !isPaused) {
      recognition.start();
    } else {
      recognition.stop();
    }

    return () => {
      recognition.stop();
    };
  }, [isListening, isPaused]);

  const toggleListening = () => {
    setIsListening(prevState => !prevState);
  };

  const togglePause = () => {
    setIsPaused(prevState => !prevState);
  };

  const clearTranscript = (index) => {
    setTranscripts(prevTranscripts => {
      const updatedTranscripts = [...prevTranscripts];
      updatedTranscripts[index] = '';
      return updatedTranscripts;
    });
  };

  return (
    <div>
      {[...Array(5)].map((_, index) => (
        <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
          <button onClick={toggleListening} style={{ backgroundColor: isListening ? 'red' : 'blue', border: 'none', marginRight: '5px' }}>
            {isListening ? <MdPause size={24} onClick={togglePause} /> : <MdMic size={24} />} 
          </button>
          <button onClick={() => clearTranscript(index)} style={{ backgroundColor: 'gray', border: 'none', marginRight: '5px' }}>
            <MdStop size={24} />
          </button>
          <label>{transcripts[index]}</label>
        </div>
      ))}
    </div>
  );
};

export default SpeechToText;
