import React, { useState, useEffect } from 'react';
import { MdMic, MdPause, MdStop } from 'react-icons/md'; // Import microphone, pause, and stop icons from react-icons library

const SpeechToText = () => {
  const [transcript1, setTranscript1] = useState('');
  const [transcript2, setTranscript2] = useState('');
  const [transcript3, setTranscript3] = useState('');
  const [transcript4, setTranscript4] = useState('');
  const [transcript5, setTranscript5] = useState('');

  const [isListening1, setIsListening1] = useState(false);
  const [isListening2, setIsListening2] = useState(false);
  const [isListening3, setIsListening3] = useState(false);
  const [isListening4, setIsListening4] = useState(false);
  const [isListening5, setIsListening5] = useState(false);

  useEffect(() => {
    const recognition1 = new window.webkitSpeechRecognition();
    recognition1.continuous = true;
    recognition1.interimResults = true;
    recognition1.lang = 'en-US';

    recognition1.onstart = () => {
      setIsListening1(true);
    };

    recognition1.onend = () => {
      setIsListening1(false);
    };

    recognition1.onresult = event => {
      const transcript = Array.from(event.results)
        .map(result => result[0].transcript)
        .join('');
      setTranscript1(transcript);
    };

    if (isListening1) {
      recognition1.start();
    } else {
      recognition1.stop();
    }

    return () => {
      recognition1.stop();
    };
  }, [isListening1]);

  // Repeat useEffect for other instances of SpeechToText with recognition2, recognition3, ...

  const toggleListening1 = () => {
    setIsListening1(prevState => !prevState);
  };

  // Repeat toggleListening for other instances of SpeechToText with toggleListening2, toggleListening3, ...

  const clearTranscript1 = () => {
    setTranscript1('');
  };

  // Repeat clearTranscript for other instances of SpeechToText with clearTranscript2, clearTranscript3, ...

  return (
    <div>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
        <button onClick={toggleListening1} style={{ backgroundColor: isListening1 ? 'red' : 'blue', border: 'none', marginRight: '5px' }}>
          {isListening1 ? <MdPause size={24} /> : <MdMic size={24} />} {/* Render microphone or pause icon based on listening state */}
        </button>
        <button onClick={clearTranscript1} style={{ backgroundColor: 'gray', border: 'none', marginRight: '5px' }}>
          <MdStop size={24} /> {/* Render stop icon */}
        </button>
        <label>{transcript1}</label> {/* Display the recognized speech transcript as label text */}
      </div>
      {/* Repeat the above div block for other instances of SpeechToText with transcript2, transcript3, ... */}
    </div>
  );
};

export default SpeechToText;
