import React, { useState, useEffect } from 'react';
import { MdMic, MdMicOff } from 'react-icons/md'; // Import microphone icons from react-icons library

const SpeechToText = () => {
  const [transcript, setTranscript] = useState('');
  const [isListening, setIsListening] = useState(false);

  useEffect(() => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onresult = event => {
      const transcript = Array.from(event.results)
        .map(result => result[0].transcript)
        .join('');
      setTranscript(transcript);
    };

    if (isListening) {
      recognition.start();
    } else {
      recognition.stop();
    }

    return () => {
      recognition.stop();
    };
  }, [isListening]);

  const toggleListening = () => {
    setIsListening(prevState => !prevState);
  };

  return (
    <div>
      <button onClick={toggleListening} style={{ backgroundColor: isListening ? 'red' : 'blue' }}>
        {isListening ? <MdMicOff /> : <MdMic />} {/* Render microphone icons based on the listening state */}
      </button>
      <label>{transcript}</label> {/* Display the recognized speech transcript as label text */}
    </div>
  );
};

export default SpeechToText;
