import React, { useState, useEffect } from 'react';
import { MdMic, MdPause, MdStop } from 'react-icons/md'; 

const SpeechToText = () => {
  const [transcripts, setTranscripts] = useState(Array(5).fill(''));
  const [isListening, setIsListening] = useState(Array(5).fill(false));
  const [buttonDisabled, setButtonDisabled] = useState(Array(5).fill(false));
  const [timer, setTimer] = useState(null);
  const HOLD_THRESHOLD = 1000; // 1 second threshold

  useEffect(() => {
    const recognitions = Array(5).fill(null).map(() => {
      const recognition = new window.webkitSpeechRecognition();
      recognition.continuous = true;
      recognition.interimResults = true;
      recognition.lang = 'en-US';
      return recognition;
    });

    recognitions.forEach((recognition, index) => {
      recognition.onstart = () => {
        setIsListening(prevState => [...prevState.slice(0, index), true, ...prevState.slice(index + 1)]);
      };

      recognition.onend = () => {
        setIsListening(prevState => [...prevState.slice(0, index), false, ...prevState.slice(index + 1)]);
      };

      recognition.onresult = event => {
        const transcript = Array.from(event.results)
          .map(result => result[0].transcript)
          .join('');
        setTranscripts(prevTranscripts => [...prevTranscripts.slice(0, index), transcript, ...prevTranscripts.slice(index + 1)]);
      };

      if (isListening[index]) {
        recognition.start();
      } else {
        recognition.stop();
      }
    });

    return () => {
      recognitions.forEach(recognition => recognition.stop());
    };
  }, [isListening]);

  const startHoldTimer = (index, action) => {
    setTimer(setTimeout(() => {
      action(index);
      setButtonDisabled(prevState => {
        const newState = [...prevState];
        newState[index] = true;
        return newState;
      });
    }, HOLD_THRESHOLD));
  };

  const cancelHoldTimer = () => {
    clearTimeout(timer);
    setTimer(null);
  };

  const toggleListening = index => {
    if (!buttonDisabled[index]) {
      setIsListening(prevState => [...prevState.slice(0, index), !prevState[index], ...prevState.slice(index + 1)]);
    }
  };

  const clearTranscript = index => {
    if (!buttonDisabled[index]) {
      setTranscripts(prevTranscripts => [...prevTranscripts.slice(0, index), '', ...prevTranscripts.slice(index + 1)]);
    }
  };

  return (
    <div>
      {transcripts.map((transcript, index) => (
        <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
          <button 
            onMouseDown={() => startHoldTimer(index, toggleListening)} 
            onMouseUp={cancelHoldTimer} 
            onTouchStart={() => startHoldTimer(index, toggleListening)} 
            onTouchEnd={cancelHoldTimer}
            disabled={buttonDisabled[index]} 
            style={{ backgroundColor: isListening[index] ? 'red' : 'blue', border: 'none', marginRight: '5px' }}
          >
            {isListening[index] ? <MdPause size={24} /> : <MdMic size={24} />}
          </button>
          <button 
            onMouseDown={() => startHoldTimer(index, clearTranscript)} 
            onMouseUp={cancelHoldTimer} 
            onTouchStart={() => startHoldTimer(index, clearTranscript)} 
            onTouchEnd={cancelHoldTimer}
            disabled={buttonDisabled[index]} 
            style={{ backgroundColor: 'gray', border: 'none', marginRight: '5px' }}
          >
            <MdStop size={24} />
          </button>
          <label>{transcript}</label>
        </div>
      ))}
    </div>
  );
};

export default SpeechToText;
