import React, { useState, useEffect } from 'react';
import { MdMic, MdPause, MdStop } from 'react-icons/md';

const SpeechToText = () => {
  const [transcripts, setTranscripts] = useState(['', '', '', '', '']);
  const [isListening, setIsListening] = useState(false);

  useEffect(() => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onresult = event => {
      const newTranscripts = Array.from(event.results)
        .map(result => result[0].transcript);
      setTranscripts(newTranscripts);
    };

    if (isListening) {
      recognition.start();
    } else {
      recognition.stop();
    }

    return () => {
      recognition.stop();
    };
  }, [isListening]);

  const toggleListening = () => {
    setIsListening(prevState => !prevState);
  };

  const clearTranscript = () => {
    setTranscripts(['', '', '', '', '']);
  };

  return (
    <div>
      {[...Array(5)].map((_, index) => (
        <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
          <button onClick={toggleListening} style={{ backgroundColor: isListening ? 'red' : 'blue', border: 'none', marginRight: '5px' }}>
            {isListening ? <MdPause size={24} /> : <MdMic size={24} />}
          </button>
          <button onClick={clearTranscript} style={{ backgroundColor: 'gray', border: 'none', marginRight: '5px' }}>
            <MdStop size={24} />
          </button>
          <label>{transcripts[index]}</label>
        </div>
      ))}
    </div>
  );
};

export default SpeechToText;
