// SpeechToText.js
import React, { useState, useEffect } from 'react';

const SpeechToText = () => {
  const [transcript, setTranscript] = useState('');
  const [isListening, setIsListening] = useState(false);

  useEffect(() => {
    const recognition = new window.webkitSpeechRecognition(); // Using WebkitSpeechRecognition for Chrome
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US'; // Set the language

    recognition.onstart = () => {
      setIsListening(true);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onresult = event => {
      const transcript = Array.from(event.results)
        .map(result => result[0].transcript)
        .join('');
      setTranscript(transcript);
    };

    if (isListening) {
      recognition.start();
    } else {
      recognition.stop();
    }

    return () => {
      recognition.stop();
    };
  }, [isListening]);

  const toggleListening = () => {
    setIsListening(prevState => !prevState);
  };
  return (
    <div>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <button onClick={toggleListening} style={{ backgroundColor: isListening ? 'red' : 'blue', border: 'none' }}>
          <MdMic size={24} style={{ marginRight: '5px' }} /> {/* Render microphone icon with a specific size */}
        </button>
        <label>{transcript}</label> {/* Display the recognized speech transcript as label text */}
      </div>
    </div>
  );
};

export default SpeechToText;
